import React, {Component} from "react";
import axios from "axios";
import {Row, Col, Nav} from "react-bootstrap";
import {Route, Switch, NavLink} from "react-router-dom";

import "./products.css"
// import Kitchen from "./Categories/homeKitchen";
// import Baby from "./Categories/baby";
import Health from "./Categories/health"
// import Sport from "./Categories/health"


class Products extends Component
{
	// iniziling before using
	constructor(props)
	{
		super(props);

		this.state = {
			Products: [],
			items: [],
			filterProducts:[],
			search: "",
			// currentPage: ""
		}
	}

	componentDidMount()
	{
		axios.get("https://demo8421975.mockable.io/products").then(res => {
			this.setState({items: res.data.products});
			return res;
		}).then(res2=>{
			this.setState({Products: res2.data.products});
		})
	}

	// filtering the items with the input
	filterList (event)
	{
		this.setState({search: event.target.value})
		var updatedList = this.state.Products;
		updatedList = updatedList.filter((item)=>{
			return item.brand.toLowerCase().search(event.target.value.toLowerCase()) !== -1;
		})
		this.setState({items: updatedList});
	}

	// rendering the categories and seperating it
	CheckingCategory()
	{
		let products = this.state.Products;
		let resultCategoriesArray = [...new Set(products.map((data, i, arr) => data.bsr_category))];

		return resultCategoriesArray;
	}

	// checking for clicked category
	clickedCategory(group)
	{
		// this.setState({currentPage: group})
		let items = this.state.Products.filter(res => res.bsr_category == group)
		this.setState({filterProducts: items})
		this.setState()
	}

	render()
	{
		// let arr = [...this.state.items];
		console.log(this.state.filterProducts)
		return (
			<div>
				<Row>
					<Col md={2}>
						<h1>Categories</h1>
						<br/>
						<br/>
						<br/>
						<Nav>
							<div className="Products-nav">
								{
									this.CheckingCategory().map(res => {
										return <NavLink activeClassName="active-link"
														onClick={()=> this.clickedCategory(res)}
														to={res.replace(/ /g,'')}>{res}
										</NavLink>
									})
								}
							</div>
						</Nav>
					</Col>
					<Col>
						<div className="Product-search">
							<h1>Search Products</h1>
							<label htmlFor="product-search"/>
							<input id="product-search" placeholder="Search..." type="text" onChange={this.filterList.bind(this)}/>
						</div>
						<br/>

							{
								this.state.filterProducts.map(data =>{
									return (
										<Switch>
											<Route exact path={data.bsr_category.replace(/ /g,'')} render={()=> <Health search={data}/>}
											/>
										</Switch>
									)}
								)
							}

							{/*<Route path="{this.state.currentPath}" render={()=> <Kitchen cats={this.state.filterProducts}/>}/>*/}
							{/*<Route exact path="/Sports" render={()=> <Baby cats={this.CheckingCategory().sport}/>}/>*/}
							{/*<Route exact path="/baby" render={()=> <Sport cats={this.CheckingCategory().baby}/>}/>*/}
					</Col>
				</Row>
			</div>
		);
	}
}

export default Products;
