import React, {Component} from "react";
import "./product-item.css";
import {Button} from "react-bootstrap";
import axios from "axios";

class Items extends Component
{
	constructor(props)
	{
		super(props);

		this.state = {
			search: this.props.search,
			products: [],
			items: [],
			currentCategory: this.props.category.split(' ').join('')
		}
	}


	componentDidMount()
	{
		axios.get("https://demo8421975.mockable.io/products").then(res=>{
			this.setState({products: res.data.products})
			return res.data.products;
		}).then((products)=> {
			let items = products.filter(res2 => res2.bsr_category.split(' ').join('') === this.state.currentCategory);
			this.setState({items: items})
		})
	}

	render()
	{
		console.log(this.state.currentCategory)
		console.log(this.state.items)
		// console.log(this.state.products)
		// console.log(this.state.search)
		return (
			<div className="">
				<ul>
					<img className="Product-img" src={this.props.img} alt={this.props.brand}/>
					<p> <strong>Product name</strong>: {this.props.brand}</p>
					<p> <strong>Price</strong>: {this.props.price}Â£</p>
					<p><strong>Rating</strong>: {this.props.rate}</p>
					<Button variant="warning">
						<a target="_blank" href={this.props.link} rel="noopener noreferrer">BUY</a>
					</Button>
				</ul>
				{
					this.props.items.map(data=>{
						console.log(data)
					})
				}
			</div>
		)
	}
}

export default Items;
