	{

	}
	
	render()
	{
		this.CheckingCategory();
		// let arr = [...this.state.items];
		return (
			<div>
				<Row>
					<Col md={2}>
						<h1>Categories</h1>
						<br/>
						<br/>
						<br/>
						<Nav>
							<div className="Products-nav">
								{
									this.CheckingCategory().map(res => {
										return <NavLink onclick={()=> this.clickedCategory(res)} to={res}>{res}</NavLink>
									})
								}
								{/*<NavLink exact activeClassName="active-link" to="/health">Health</NavLink>*/}
								{/*<NavLink exact activeClassName="active-link" to="/kitchen">Kitchen</NavLink>*/}
								{/*<NavLink exact activeClassName="active-link" to="/sport">Sport</NavLink>*/}
								{/*<NavLink exact activeClassName="active-link" to="/baby">Baby</NavLink>*/}
							</div>
						</Nav>
					</Col>
					<Col>
						<div className="Product-search">
							<h1>Search Products</h1>
							<label htmlFor="product-search"/>
							<input id="product-search" placeholder="Search..." type="text" onChange={this.filterList.bind(this)}/>
						</div>
						<br/>

						<Switch>
							{/*<Route exact path="/health" render={()=> <Health search={this.state.search} cats={this.CheckingCategory().health}/>}/>*/}
							{/*<Route exact path="/kitchen" render={()=> <Kitchen cats={this.CheckingCategory().kitchen}/>}/>*/}
							{/*<Route exact path="/sport" render={()=> <Baby cats={this.CheckingCategory().sport}/>}/>*/}
							{/*<Route exact path="/baby" render={()=> <Sport cats={this.CheckingCategory().baby}/>}/>*/}
						</Switch>
					</Col>
				</Row>
			</div>
		);
	}
}

export default Products;
